def total_mail(journaliste)
  puts "Voici le nombre de mails : "        # --> "Demande, Combien y a-t-il de handle dans cette array + résultat."
  puts journaliste.size  
end

def short_mail(journaliste)
  puts "Le mail le plus court est : "         # --> "Demande, Quel est le handle le plus court de cette liste + résultat."
  puts journaliste.min_by(&:size).to_s  
end

def w5_mail(journaliste)
  w5 = journaliste.find_all{|x| x.size == 6}
  puts "Mails contenant 5 caractères : "              # --> "Demande, Combien y-a-t'il de handle contenant 5 caractères + résultat."
  w5.each{ |x| puts "#{x[1..-1]}" }        
    
end  

def capitals_mail(journaliste)
  capital = journaliste.select { |word| word.start_with?( /@[[:upper:]]/ ) }
  puts "Les mails avec une majuscule sont : "                                   # --> "Demande, Combien commencent par une majuscule + résultat."
  capital.each{ |x| puts "#{x[1..-1]}" } 
end 

def order_mail(journaliste)
  order = journaliste.sort_by{ |word| word.downcase}
  puts "Trier la liste de handle par ordre alphabétique : "             # --> " Demande, Trier la liste de handle par ordre alphabétique + résultat."
  order.each{ |x| puts "#{x[1..-1]}"}
end

def increasing_mail(journaliste)
  increasing = journaliste.sort_by{|word| word.length}
  puts "Trier la liste de handle par taille des handle : "         # --> "Demande, Trier la liste de handle par taille des handle + résultat."
  increasing.each{ |x| puts "#{x[1..-1]}"}                                     
  
end

def position_mail(journaliste)
  position = journaliste.find_index{|p| p == "@epenser"}
  puts "Quelle est la position dans l'array de la personne @epenser ? : "             # --> "Demande, Quelle est la position dans l'array de la personne @epenser + résultat."
  puts position
end

def repartition_mail(journaliste)
  journaliste = journaliste.sort_by{|x| x.size}

  currentSize = journaliste[0].size                                              # --> "Demande, Sortir une répartition des handle par taille de ces derniers + résultat."
  counter = 1
      puts "Sortir une répartition des handle par taille de ces derniers : "
  journaliste.each{|j|
      if j.size == currentSize
          counter += 1
      else
          puts "Il y a #{counter} mots de #{currentSize} caracteres"
          currentSize= j.size
          counter = 1
      end
  }
end

# "Nom des méthodes pour les activer"
total_mail(journaliste)
short_mail(journaliste)
w5_mail(journaliste)
capitals_mail(journaliste)
order_mail(journaliste)
increasing_mail(journaliste)
position_mail(journaliste)
repartition_mail(journaliste)
